mapper.select:
		SELECT
		o.*,
		u.name,
		u.phone,
		c.channel,
		s.describe,
		s.button
		FROM
		orders o
		LEFT JOIN channel c ON o.channel_id = c.id
		LEFT JOIN USER u ON o.user_id = u.id
		LEFT JOIN order_status s ON o.order_status = s.status
		where 1=1
		
			AND o.id=#{id,jdbcType=INTEGER}
		
		
			AND o.order_no = #{orderNo,jdbcType=VARCHAR}
		
		
			AND u.name = #{name,jdbcType=VARCHAR}
		
		
			AND u.phone = #{phone,jdbcType=VARCHAR}
		
		
			AND o.flightNum = #{flightNum,jdbcType=VARCHAR}
		
		
			AND o.user_id = #{userId,jdbcType=INTEGER}
		
		
			AND o.channel_id = #{channelId,jdbcType=INTEGER}
		
		
			AND o.abnormal_status = #{abnormalStatus,jdbcType=VARCHAR}
		
		
			AND o.order_status = #{orderStatus,jdbcType=INTEGER}
		
		
			AND o.pay_status = #{payStatus,jdbcType=VARCHAR}
		
		
			  
             and now_time <= DATE_FORMAT(#{nowTimeEnd}, '%Y/%m/%d %H:%i:%S')
              
		
		
			  
             and now_time >= DATE_FORMAT(#{nowTimeStart}, '%Y/%m/%d')
            
		
		
			and o.id IN
			
				#{snss,jdbcType=INTEGER}
			
		
		
			and o.order_status IN
			
				#{snss,jdbcType=INTEGER}
			
		
		ORDER BY
		o.create_date desc
